<?php defined('BASEPATH') OR exit('No direct script access allowed');class People extends CI_Controller {	public function __construct()	{		parent::__construct();		$this->load->model('People_model');		//Load Pagination		$this->load->library('pagination');	}	public function index()	{		$data['title'] = $this->Title_model->setTitle('Peoples List Page');		// -----------------------------------Searching Is Coming Soon-------------------------------------------				// // Searching 		// if (isset($_POST['submit'])) {		// 	$data['keyword'] = $this->input->post('keyword');		// }		// else {		// 	$data['keyword'] = null;		// }			//Config Pagination		$config['total_rows'] = $this->People_model->getCountData();		$config['per_page'] = 12;		$config['uri_segement'] = $this->uri->segment(3);		$data['start_index'] = $this->uri->segment(3);		//Initialize Config		$this->pagination->Initialize($config);		$data['people'] = $this->People_model->getPeople($config['uri_segement'], $config['per_page']);		$this->load->view('templates/header', $data);		$this->load->view('People/index', $data);		$this->load->view('templates/footer');	}	public function delete($id)	{		$this->session->set_flashdata('flash', 'Deleted');		$this->People_model->deletePeep($id);		redirect(base_url(). 'people');	}}/* End of file People.php *//* Location: ./application/controllers/People.php */